package beakJ01;

import java.util.Scanner;

public class N_10869 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x, y;
		
		Scanner scanner = new Scanner(System.in);
		
		x = scanner.nextInt();
		y = scanner.nextInt();
		
		System.out.println(x + y);
		System.out.println(x - y);
		System.out.println(x * y);
		System.out.println(x / y);
		System.out.println(x % y);
		
		scanner.close();
		

	}

}
